        _id
        firstName
        lastName
        phoneNumber
        email
      }
    }
    Corporate {
      _id
      businessCode
      businessName
    }
    createdThrough {
      versionCode
      versionName
      userPlatform
    }
    supplyQuote {
      amount
      source
